document.addEventListener("DOMContentLoaded", () => {
    const themeIcon = document.getElementById("theme-icon");
    const currentTheme = localStorage.getItem("theme") || "light-theme";

    document.body.classList.add(currentTheme);
    updateIcon(currentTheme);

    themeIcon.addEventListener("click", () => {
        const newTheme = document.body.classList.contains("light-theme") ? "dark-theme" : "light-theme";
        document.body.classList.remove("light-theme", "dark-theme");
        document.body.classList.add(newTheme);
        localStorage.setItem("theme", newTheme);
        updateIcon(newTheme);
    });

    function updateIcon(theme) {
        if (theme === "dark-theme") {
            themeIcon.classList.replace("fa-moon", "fa-sun");
            themeIcon.style.color = "#fff";
            // themeIcon.style.color = "#ffd106";
        } else {
            themeIcon.classList.replace("fa-sun", "fa-moon");
            // themeIcon.style.color = "#333";
            themeIcon.style.color = "#2563eb";
        }
    }
});

// document.addEventListener("contextmenu", (event) => event.preventDefault());

document.addEventListener("keydown", (event) => {
    if (
        event.key === "F12" || // ป้องกัน F12
        (event.ctrlKey && event.shiftKey && event.key === "I") || // ป้องกัน Ctrl+Shift+I
        (event.ctrlKey && event.shiftKey && event.key === "J") || // ป้องกัน Ctrl+Shift+J
        (event.ctrlKey && event.key === "U") || // ป้องกัน Ctrl+U (View Source)
        (event.ctrlKey && event.key === "C") || // ป้องกัน Ctrl+C
        (event.ctrlKey && event.key === "V") // ป้องกัน Ctrl+V
    ) {
        event.preventDefault();
    }
});